#include "Vbooth.h" // Generated by Verilator
#include "verilated.h"

#include "testbench.h"

unsigned int input1[10] = {10, 100, 1000, 0xFFFFFFFF, 0, 8, 2, 17, 29, 33};
unsigned int input2[10] = {3, 5, 7, 9, 22, 0xFFFFFFFF, 21, 37, 49, 11345678};

// Main
int main(int argc, char **argv)
{
    Verilated::commandArgs(argc, argv);
    Testbench<Vbooth> tb("Booth-Multiplier");
    tb.reset();

    tb.tick();

    for (int op = 0; op < 4; op++)
    {
        switch (op)
        {
        case 0: // MULxU
            tb.dut->X_signed = 0;
            tb.dut->Y_signed = 0;
            tb.set_case("Unsigned X Unsigned");
            break;
        case 1: // (unsupported by risc V)
            tb.dut->X_signed = 0;
            tb.dut->Y_signed = 1;
            tb.set_case("Unsigned X   Signed");
            break;
        case 2: // MULxSU
            tb.dut->X_signed = 1;
            tb.dut->Y_signed = 0;
            tb.set_case("Signed X Unsigned");
            break;
        case 3: // MULx
            tb.dut->X_signed = 1;
            tb.dut->Y_signed = 1;
            tb.set_case("Signed X   Signed");
            break;
        }

        // First set of inputs
        for (int i = 0; i < 10; i++)
        {
            for (int ii = 0; ii < 10; ii++)
            {
                // Set inputs
                tb.dut->X = input1[i];
                tb.dut->Y = input2[ii];

                // Start the test
                tb.stick();
                tb.dut->start = 1;
                tb.stick();

                tb.run_until(&tb.dut->valid, 1);

                tb.check_equality((unsigned int)tb.dut->valid, (unsigned int)1, "Valid");

                switch (op)
                {
                case 0:
                    tb.check_equality((unsigned)tb.dut->Z, (unsigned)tb.dut->X * (unsigned)tb.dut->Y, "Result (U * U)");
                    break;
                case 1:
                    tb.check_equality((signed)tb.dut->Z, (unsigned)tb.dut->X * (signed)tb.dut->Y, "Result (U * S)");
                    break;
                case 2:
                    tb.check_equality((signed)tb.dut->Z, (signed)tb.dut->X * (unsigned)tb.dut->Y, "Result (S * U)");
                    break;
                case 3:
                    tb.check_equality((signed)tb.dut->Z, (signed)tb.dut->X * (signed)tb.dut->Y, "Result (S * S)");
                    break;
                }

                tb.tick();
                tb.check_equality((unsigned int)tb.dut->valid, 0, "Valid");
                tb.increment_cycles();
            }
        }
    }

    return tb.get_return();
}
