#include "Voccupancy.h" // Generated by Verilator
#include "verilated.h"
#include "verilated_vcd_c.h"

#include <iostream>
#include <iomanip>

#include "colors.h"
#include "utils.h"

char *module = (char *)"PCounter";

// Main
int main(int argc, char **argv)
{
    Verilated::commandArgs(argc, argv);

    // Instantiate model
    Voccupancy *tb = new Voccupancy;

    // Setup waveform tracing
    Verilated::traceEverOn(true);
    VerilatedVcdC *tfp = new VerilatedVcdC;
    tb->trace(tfp, 99);
    tfp->open("simout/occupancy.vcd");

    // Reset sequence
    tb->clk = 0;
    tb->rst_n = 1;
    stick(tb, tfp);
    tb->rst_n = 0;
    stick(tb, tfp);
    tb->rst_n = 1;
    tick(tb, tfp);

    initial_print(module);

    int cycle = 0;

    // First, lock some registers
    for (int i = 1; i < 32; i += 2)
    {
        tb->source1 = 0;
        tb->source2 = 0;
        tb->target = i;
        tb->lock = 1;
        tick(tb, tfp);

        equality_print((char *)"Execution status bit (1)", cycle, tb->exec_ok, 1);
        cycle += 1;
    }

    // Then, try to overwrite the same registers (shall be blocked).
    for (int i = 1; i < 32; i += 2)
    {
        tb->source1 = 0;
        tb->source2 = 0;
        tb->target = i;
        tb->lock = 1;
        tick(tb, tfp);

        equality_print((char *)"Execution status bit (0)", cycle, tb->exec_ok, 0);
        cycle += 1;
    }
    tb->lock = 0;

    // Then, free them
    for (int i = 1; i < 32; i += 2)
    {
        tb->address = i;
        tb->write = 1;
        tick(tb, tfp);
        cycle += 1;
    }
    tb->write = 0;

    // Finally, relock them
    for (int i = 1; i < 32; i += 2)
    {
        tb->source1 = 0;
        tb->source2 = 0;
        tb->target = i;
        tb->lock = 1;
        tick(tb, tfp);

        equality_print((char *)"Execution status bit (1)", cycle, tb->exec_ok, 1);
        cycle += 1;
    }

    final_print(module);

    tfp->close();

    uint64_t fail, pass;
    get_counts(&pass, &fail);

    delete tb;
    return (fail != 0) ? 1 : 0;
}
