#include "Vbooth.h" // Generated by Verilator
#include "verilated.h"
#include "verilated_vcd_c.h"

#include <iostream>
#include <iomanip>

#include "utils/colors.h"
#include "utils/utils.h"
#include "generated_commands.h"

char *module = (char *)"Booth-Multiplier";

unsigned int input1[10] = {10, 100, 1000, 0xFFFFFFFF, 0, 8, 2, 17, 29, 33};
unsigned int input2[10] = {3, 5, 7, 9, 22, 0xFFFFFFFF, 21, 37, 49, 11345678};

// Main
int main(int argc, char **argv)
{
    Verilated::commandArgs(argc, argv);

    // Instantiate model
    Vbooth *tb = new Vbooth;

    // Setup waveform tracing
    Verilated::traceEverOn(true);
    VerilatedVcdC *tfp = new VerilatedVcdC;
    tb->trace(tfp, 99);
    tfp->open("simout/booth.vcd");

    // Reset sequence
    stick(tb, tfp);
    tb->rst_n = 0;
    tb->clk = 0;
    tick(tb, tfp);
    tick(tb, tfp);

    tb->rst_n = 1;
    tick(tb, tfp);

    initial_print(module);

    int ticks = 0;

    for (int op = 0; op < 4; op ++)
    {
        switch (op)
        {
            case 0 :                // MULxU
                tb->X_signed = 0;
                tb->Y_signed = 0;
                break;
            case 1 :                // (unsupported by risc V)
                tb->X_signed = 0;
                tb->Y_signed = 1;
                break;
            case 2 :                // MULxSU
                tb->X_signed = 1;
                tb->Y_signed = 0;
                break;
            case 3 :                // MULx
                tb->X_signed = 1;
                tb->Y_signed = 1;
                break;
        }

        // First set of inputs
        for (int i = 0; i < 10; i++)
        {
            for (int ii = 0; ii < 10; ii ++)
            {
                // Set inputs
                tb->X = input1[i];
                tb->Y = input2[ii];

                // Start the test
                stick(tb, tfp);
                tb->start = 1;
                stick(tb, tfp);

                // Let the testbench compute
                for (int i = 0; i < 33; i++)
                {
                    tick(tb, tfp);
                    tb->start = 0;
                }
                equality_print((char *)"Valid         ",
                                ticks,
                                tb->valid,
                                1);

                switch(op)
                {
                    case 0 : 
                        equality_print((char *)"Result (U * U)",
                                        ticks,
                                        tb->Z,
                                        (unsigned)tb->X * (unsigned)tb->Y);
                        break;
                    case 1 :
                        equality_print((char *)"Result (U * S)",
                                        ticks,
                                        tb->Z,
                                        (unsigned)tb->X * (signed)tb->Y);
                        break;
                    case 2 :
                        equality_print((char *)"Result (S * U)",
                                        ticks,
                                        tb->Z,
                                        (signed)tb->X * (unsigned)tb->Y);
                        break;
                    case 3 : 
                        equality_print((char *)"Result (S * S)",
                                        ticks,
                                        tb->Z,
                                        (signed)tb->X * (signed)tb->Y);
                        break;
                }

                tick(tb, tfp);
                equality_print((char *)"Valid         ",
                                ticks,
                                tb->valid,
                                0);

                ticks += 1;
            }
        }
    }

    final_print(module);

    tfp->close();

    uint64_t pass, fail;
    get_counts(&pass, &fail);
    delete tb;
    return fail;
}
