#include "Vclock.h" // Generated by Verilator
#include "verilated.h"
#include "verilated_vcd_c.h"
#include <iostream>
#include <iomanip>
#include "utils/colors.h"

// Simulation time
vluint64_t sim_time = 0;
int clkdiv;

// Toggle clock helper
void tick(Vclock *tb, VerilatedVcdC *tfp)
{
    tb->clk = 0;
    tb->eval();
    tfp->dump(sim_time++);
    tb->clk = 1;
    tb->eval();
    tfp->dump(sim_time++);
}

// Main
int main(int argc, char **argv)
{
    Verilated::commandArgs(argc, argv);

    // Instantiate model
    Vclock *tb = new Vclock;

    // Setup waveform tracing
    Verilated::traceEverOn(true);
    VerilatedVcdC *tfp = new VerilatedVcdC;
    tb->trace(tfp, 99);
    tfp->open("simout/clock.vcd");

    // Reset sequence
    tb->rst_n = 0;
    tb->clk_en = 0;
    tb->clk = 0;
    tick(tb, tfp);
    tick(tb, tfp);

    tb->rst_n = 1;
    tick(tb, tfp);

    std::cout << KMAG
              << "Starting counter simulation...\n"
              << RST;

    int pass = 0;
    int fail = 0;

    int ticks = 0;

    // Running for a defined number of cycles
    for (int i = 0; i < 1000; i++)
    {
        tick(tb, tfp);
        ticks += 1;
        int rem = !(ticks % 2);

        if (ticks > 1)
        {
            if (tb->clk_en == rem)
            {
                pass += 1;
            }
            else
            {
                fail += 1;
            }
        }
    }

    std::cout << KMAG
              << "Simulation complete."
              << std::endl
              << std::dec
              << KYEL << "--------------------------------------------------------\n"
              << "Results : (Clocks)"
              << "\n--------------------------------------------------------"
              << std::endl
              << KGRN << "\tPass : "
              << std::setw(4) << pass
              << KRED << "\n\tFail : "
              << std::setw(4) << fail
              << RST
              << std::endl;

    if (fail == 0)
        std::cout << KGRN
                  << "Tests passed !"
                  << RST
                  << std::endl;
    else
        std::cout << KRED
                  << "Tests failed !"
                  << RST
                  << std::endl;

    tfp->close();
    delete tb;
    return fail;
}
